// Please note: Do not remove this line or asset references may break.
/**
 * @param {String} value (for property 'text')
 */
export var scriptProperties = createScriptProperties()
	// Whether you want to see the seconds or not
	.addCheckbox({
		name: 'showYear',
		label: '显示年份',
		value: true
	})
	// This will be used to separate each element
	.addText({
		name: 'delimiter',
		label: '定界符',
		value: ':'
	})
	.addCheckbox({
		name: 'showDate',
		label: '不显示日期',
		value: false
	})
	.addCheckbox({
		name: 'showCD',
		label: '隐藏日期时间',
		value: false
	})
	.finish();
export function update(value) {
	let now = new Date();
	var year = now.getFullYear();
	let month = ("00" + (now.getMonth()+1)).slice(-2);
	let day = ("00" + now.getDate()).slice(-2);
	value = year + scriptProperties.delimiter + month + scriptProperties.delimiter + day;
	if (!scriptProperties.showYear) {
		value = month + scriptProperties.delimiter +day;
	}
	if (scriptProperties.showDate) {
		value = '\0';
	}
	if (scriptProperties.showCD) {
		value = '\0';
	}
	return value;
}

