export var scriptProperties = createScriptProperties()
	.addCheckbox({
		name: 'use12hFormat',
		label: '使用12时',
		value: false
	})
	// This will be used to separate each element
	.addText({
		name: 'delimiter',
		label: '定界符',
		value: ':'
	})
	.addCheckbox({
		name: 'showTime',
		label: '不显示时间',
		value: false
	})
	.addCheckbox({
		name: 'showCD',
		label: '隐藏日期时间',
		value: false
	})
	.finish();
/**
 * @param {String} value (for property 'text')
 */
export function update(value) {
	let time = new Date();
	var hours = time.getHours();
	if (scriptProperties.use12hFormat) {
		hours %= 12;
		if (hours == 0) {
			hours = 12;
		}
	}
	hours = ("00" + hours).slice(-2);
	let minutes = ("00" + time.getMinutes()).slice(-2);
	value = hours + scriptProperties.delimiter + minutes;
	if (scriptProperties.showTime) {
		value = '\0';
	}
	if (scriptProperties.showCD) {
		value = '\0';
	}
	return value;
}

